import React, { useState, useEffect } from 'react';

export function PersonsComponent() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [persons, setItems] = useState([]);

    useEffect(() => {
      fetch("http://localhost:8082/persons")
        .then(res => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
            setItems(result.content);
          },

          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        )
    }, [])

    if (error) {
      return <tbody>Error: {error.message}</tbody>;
    } else if (!isLoaded) {
      return <tbody>Loading...</tbody>;
    } else {
      return (
        <tbody>{
            persons.map(person => 
            <tr key={person.id}>
                <td>{person.prenom}</td>
                <td>{person.nom}</td>
                <td>{person.dateNaissance}</td>
                <td>{person.sexe}</td>
            </tr>
            )
        }
        </tbody>
      );
    }
}
